// Parse SearxNG HTML response to extract news results
const htmlContent = $input.first().binary.data.toString();

// Simple HTML parsing to extract search results
const results = [];

// Extract result blocks using regex patterns
const resultPattern = /<article[^>]*class="[^"]*result[^"]*"[^>]*>([\s\S]*?)<\/article>/gi;
let match;

while ((match = resultPattern.exec(htmlContent)) !== null) {
  const resultHtml = match[1];

  // Extract title (remove HTML tags and highlights)
  const titleMatch = /<h3[^>]*>\s*<a[^>]*>([\s\S]*?)<\/a>\s*<\/h3>/i.exec(resultHtml);
  let title = titleMatch ? titleMatch[1].trim() : '';
  // Remove highlight spans
  title = title.replace(/<span[^>]*class="highlight"[^>]*>([^<]+)<\/span>/gi, '$1');
  title = title.replace(/<[^>]+>/g, '').trim();

  // Extract URL
  const urlMatch = /<h3[^>]*>\s*<a[^>]*href="([^"]+)"/i.exec(resultHtml);
  const url = urlMatch ? urlMatch[1] : '';

  // Extract content/description (remove HTML tags and highlights)
  const contentMatch = /<p[^>]*class="[^"]*content[^"]*"[^>]*>([\s\S]*?)<\/p>/i.exec(resultHtml);
  let content = contentMatch ? contentMatch[1].trim() : '';
  // Remove highlight spans
  content = content.replace(/<span[^>]*class="highlight"[^>]*>([^<]+)<\/span>/gi, '$1');
  content = content.replace(/<[^>]+>/g, '').trim();

  // Extract engine/source
  const engineMatch = /<div[^>]*class="engines"[^>]*>[\s\S]*?<span>([^<]+)<\/span>/i.exec(resultHtml);
  const engine = engineMatch ? engineMatch[1].trim() : 'unknown';

  if (title && url) {
    results.push({
      title: title,
      url: url,
      content: content,
      engine: engine
    });
  }
}

// If no results found with the above pattern, try alternative parsing
if (results.length === 0) {
  // Try simpler pattern for title and URL extraction
  const linkPattern = /<h3[^>]*>\s*<a[^>]*href="([^"]+)"[^>]*>([\s\S]*?)<\/a>/gi;
  let linkMatch;

  while ((linkMatch = linkPattern.exec(htmlContent)) !== null) {
    let title = linkMatch[2].trim();
    title = title.replace(/<span[^>]*class="highlight"[^>]*>([^<]+)<\/span>/gi, '$1');
    title = title.replace(/<[^>]+>/g, '').trim();

    results.push({
      title: title,
      url: linkMatch[1],
      content: '',
      engine: 'searxng'
    });
  }
}

return {
  results: results,
  total_results: results.length,
  parsed_from: 'html'
};

