const candlesDaily = $('Freqtrade Daily Data').first().json.data || [];
const coinNews = $('Parse Coin News HTML').first().json;
const sentiment = $('Coin Sentiment').first().json;

// Helper function to calculate technical indicators
function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) return 50;

  let gains = 0, losses = 0;
  for (let i = 1; i <= period; i++) {
    const change = prices[prices.length - i] - prices[prices.length - i - 1];
    if (change > 0) gains += change;
    else losses -= change;
  }

  const avgGain = gains / period;
  const avgLoss = losses / period;
  const rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

function calculateEMA(prices, period) {
  if (prices.length < period) return prices[prices.length - 1];

  const k = 2 / (period + 1);
  let ema = prices[prices.length - period];

  for (let i = prices.length - period + 1; i < prices.length; i++) {
    ema = (prices[i] * k) + (ema * (1 - k));
  }
  return ema;
}

function calculateMACD(prices, fast = 12, slow = 26, signal = 9) {
  if (prices.length < slow + signal) return { macd: 0, signal: 0, histogram: 0 };

  const emaFast = calculateEMA(prices, fast);
  const emaSlow = calculateEMA(prices, slow);
  const macdLine = emaFast - emaSlow;

  // Simplified signal calculation
  const macdValues = [];
  for (let i = slow; i < prices.length; i++) {
    const fastEma = calculateEMA(prices.slice(0, i + 1), fast);
    const slowEma = calculateEMA(prices.slice(0, i + 1), slow);
    macdValues.push(fastEma - slowEma);
  }

  const signalLine = calculateEMA(macdValues, signal);
  const histogram = macdLine - signalLine;

  return { macd: macdLine, signal: signalLine, histogram: histogram };
}

function calculateATR(candles, period = 14) {
  if (candles.length < period) return 0;

  let trSum = 0;
  for (let i = Math.max(1, candles.length - period); i < candles.length; i++) {
    const high = candles[i][2];
    const low = candles[i][3];
    const prevClose = candles[i - 1][4];

    const tr = Math.max(
      high - low,
      Math.abs(high - prevClose),
      Math.abs(low - prevClose)
    );
    trSum += tr;
  }

  return trSum / period;
}

// Extract price data
const price = coinData.current_price;
const volume24h = coinData.total_volume;
const marketCap = coinData.market_cap;
const priceChange24h = coinData.price_change_percentage_24h;
const priceChange7d = coinData.price_change_percentage_7d;

// Calculate technical indicators for 4H timeframe
const prices4h = candles4h.map(candle => candle[4]); // closing prices
const highs4h = candles4h.map(candle => candle[2]);
const lows4h = candles4h.map(candle => candle[3]);
const volumes4h = candles4h.map(candle => candle[5]);

const rsi4h = calculateRSI(prices4h);
const macd4h = calculateMACD(prices4h);
const ema9_4h = calculateEMA(prices4h, 9);
const ema21_4h = calculateEMA(prices4h, 21);
const ema50_4h = calculateEMA(prices4h, 50);
const ema200_4h = calculateEMA(prices4h, 200);
const atr4h = calculateATR(candles4h);

// Calculate technical indicators for Daily timeframe
const pricesDaily = candlesDaily.map(candle => candle[4]);
const rsiDaily = calculateRSI(pricesDaily);
const macdDaily = calculateMACD(pricesDaily);
const ema50Daily = calculateEMA(pricesDaily, 50);
const ema200Daily = calculateEMA(pricesDaily, 200);

// Bollinger Bands calculation
const sma20 = prices4h.slice(-20).reduce((sum, price) => sum + price, 0) / 20;
const variance = prices4h.slice(-20).reduce((sum, price) => sum + Math.pow(price - sma20, 2), 0) / 20;
const stdDev = Math.sqrt(variance);
const bbUpper = sma20 + (stdDev * 2);
const bbLower = sma20 - (stdDev * 2);

// Volume analysis
const avgVolume4h = volumes4h.slice(-20).reduce((sum, vol) => sum + vol, 0) / 20;
const currentVolume = volumes4h[volumes4h.length - 1];
const volumeRatio = currentVolume / avgVolume4h;

// Sentiment Analysis
const sentimentScore = sentiment.result?.polarity || 0;
const newsCount = coinNews.results?.length || 0;

// Advanced Signal Analysis
let bullishScore = 0;
let bullishReasons = [];
let bearishScore = 0;
let bearishReasons = [];

// === BULLISH SIGNAL DETECTION ===

// 1. Multi-timeframe RSI analysis
if (rsi4h < 35 && rsiDaily < 45) {
  bullishScore += 2;
  bullishReasons.push('Multi-TF RSI oversold');
} else if (rsi4h < 40) {
  bullishScore += 1;
  bullishReasons.push('4H RSI oversold');
}

// 2. MACD Analysis
if (macd4h.macd > macd4h.signal && macdDaily.macd > macdDaily.signal) {
  bullishScore += 2;
  bullishReasons.push('Multi-TF MACD bullish');
} else if (macd4h.macd > macd4h.signal) {
  bullishScore += 1;
  bullishReasons.push('4H MACD cross up');
}

// 3. EMA Structure Analysis
if (price > ema9_4h && ema9_4h > ema21_4h && ema21_4h > ema50_4h && ema50Daily > ema200Daily) {
  bullishScore += 3;
  bullishReasons.push('Perfect EMA alignment');
} else if (price > ema21_4h && ema50Daily > ema200Daily) {
  bullishScore += 2;
  bullishReasons.push('Bullish EMA structure');
} else if (price > ema50_4h) {
  bullishScore += 1;
  bullishReasons.push('Above key EMAs');
}

// 4. Bollinger Bands Analysis
if (price < bbLower && rsi4h < 35) {
  bullishScore += 2;
  bullishReasons.push('BB oversold + RSI');
}

