  bearishScore += 3;
  bearishReasons.push('Perfect EMA bearish alignment');
} else if (price < ema21_4h && ema50Daily < ema200Daily) {
  bearishScore += 2;
  bearishReasons.push('Bearish EMA structure');
} else if (price < ema50_4h) {
  bearishScore += 1;
  bearishReasons.push('Below key EMAs');
}

// 4. Bollinger Bands Analysis
