  bearishScore += 1;
  bearishReasons.push('Negative sentiment + news');
}

// 7. Market Structure
if (marketContext.marketStructure.bearish_ratio > 0.6 && marketContext.marketOverview.fear_greed_index < 30) {
  bearishScore += 1;
  bearishReasons.push('Bearish market structure');
}

// === SIGNAL VALIDATION ===
let direction = null;
let finalScore = 0;
let finalReasons = [];
let confidence = 0;

if (bullishScore >= 6) {
  direction = 'LONG';
  finalScore = bullishScore;
  finalReasons = bullishReasons;
  confidence = Math.min(finalScore * 10 + 20, 85);
} else if (bearishScore >= 6) {
  direction = 'SHORT';
  finalScore = bearishScore;
  finalReasons = bearishReasons;
  confidence = Math.min(finalScore * 10 + 20, 85);
}

// Calculate entry, stop loss, and take profits using ATR
if (direction && atr4h > 0) {
  let entry, stopLoss, takeProfit1, takeProfit2;
  const atrMultiplier = Math.max(atr4h, price * 0.02); // Minimum 2% or ATR

  if (direction === 'LONG') {
    entry = price;
    stopLoss = price - (atrMultiplier * 2);
    takeProfit1 = price + (atrMultiplier * 3);
    takeProfit2 = price + (atrMultiplier * 5);
  } else {
    entry = price;
    stopLoss = price + (atrMultiplier * 2);
    takeProfit1 = price - (atrMultiplier * 3);
    takeProfit2 = price - (atrMultiplier * 5);
  }

  return {
    signal_found: true,
    symbol: coinData.symbol.toUpperCase(),
    name: coinData.name,
