// Filter and prepare coins for analysis
const coins = $('Top 50 Coins').first().json;
const marketSentiment = $('Market Sentiment').first().json.data[0];

// Filter coins based on quality criteria
const qualityCoins = coins.filter(coin => {
  return coin.market_cap > 500000000 && // $500M+ market cap
         coin.total_volume > 50000000 && // $50M+ 24h volume
         coin.current_price > 0.01 && // Avoid extremely low price coins
         coin.market_cap_rank <= 30 && // Top 30 only for highest quality
         // Focus on major, liquid coins
         ['BTC', 'ETH', 'BNB', 'XRP', 'ADA', 'SOL', 'DOT', 'MATIC', 'LTC', 'AVAX', 
          'LINK', 'UNI', 'ATOM', 'XLM', 'FIL', 'TRX', 'ETC', 'THETA', 'VET', 'ICP',
          'DOGE', 'SHIB', 'CRO', 'NEAR', 'APT', 'OP', 'ARB'].includes(coin.symbol.toUpperCase());
}).slice(0, 15); // Limit to top 15 for focused analysis

// Market context for scoring adjustments
const marketContext = {
  fear_greed: marketSentiment.value,
  fear_greed_class: marketSentiment.value_classification,
  market_direction: marketSentiment.value > 50 ? 'bullish' : 'bearish',
  total_market_cap: coins.reduce((sum, coin) => sum + coin.market_cap, 0),
  gainers_count: coins.filter(c => c.price_change_percentage_24h > 0).length,
  losers_count: coins.filter(c => c.price_change_percentage_24h < 0).length
};

return qualityCoins.map(coin => ({
  ...coin,
  marketContext: marketContext
}));

// Enhanced technical analysis using multiple timeframes
const coinData = $('Split for Analysis').item.json;
const candles1h = $('Freqtrade 1H Data').first().json.data || [];
const candles4h = $('Freqtrade 4H Data').first().json.data || [];
const candlesDaily = $('Freqtrade Daily Data').first().json.data || [];
const newsHtml = $('Coin News').first().binary.data.toString();

// Helper functions for technical indicators
function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) return 50;

  let gains = 0, losses = 0;
  for (let i = 1; i <= period; i++) {
    const change = prices[prices.length - i] - prices[prices.length - i - 1];
    if (change > 0) gains += change;
    else losses -= change;
  }

  const avgGain = gains / period;
  const avgLoss = losses / period;
  if (avgLoss === 0) return avgGain > 0 ? 100 : 50;
  const rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

function calculateEMA(prices, period) {
  if (prices.length < period) return prices[prices.length - 1] || 0;

  const k = 2 / (period + 1);
  let ema = prices[prices.length - period];

  for (let i = prices.length - period + 1; i < prices.length; i++) {
    ema = (prices[i] * k) + (ema * (1 - k));
  }
  return ema;
}

function calculateMACD(prices, fast = 12, slow = 26) {
  if (prices.length < slow) return { macd: 0, signal: 0, histogram: 0 };

  const emaFast = calculateEMA(prices, fast);
  const emaSlow = calculateEMA(prices, slow);
  const macdLine = emaFast - emaSlow;

  // Calculate signal line (9-period EMA of MACD)
  const macdValues = [];
  for (let i = slow; i < prices.length; i++) {
    const fastEma = calculateEMA(prices.slice(0, i + 1), fast);
    const slowEma = calculateEMA(prices.slice(0, i + 1), slow);
    macdValues.push(fastEma - slowEma);
  }

  const signalLine = calculateEMA(macdValues, 9);
  const histogram = macdLine - signalLine;

  return { macd: macdLine, signal: signalLine, histogram: histogram };
}

function parseNewsForSentiment(html) {
  const results = [];
  const resultPattern = /<article[^>]*class="[^"]*result[^"]*"[^>]*>([\s\S]*?)<\/article>/gi;
  let match;

  while ((match = resultPattern.exec(html)) !== null) {
    const resultHtml = match[1];

    const titleMatch = /<h3[^>]*>\s*<a[^>]*>([\s\S]*?)<\/a>\s*<\/h3>/i.exec(resultHtml);
    let title = titleMatch ? titleMatch[1].trim() : '';
    title = title.replace(/<span[^>]*class="highlight"[^>]*>([^<]+)<\/span>/gi, '$1');
    title = title.replace(/<[^>]+>/g, '').trim();

    if (title) results.push(title);
  }

  return results.slice(0, 10); // Top 10 headlines
}

function calculateSentimentScore(headlines, coin) {
  if (!headlines || headlines.length === 0) return 0;

  const positiveWords = ['bullish', 'pump', 'rally', 'surge', 'gains', 'up', 'rise', 'breakout', 'moon', 'all-time', 'high', 'partnership', 'adoption', 'upgrade'];
  const negativeWords = ['bearish', 'dump', 'crash', 'fall', 'down', 'drop', 'correction', 'sell-off', 'decline', 'hack', 'scam', 'regulation', 'ban'];

  let score = 0;
  const text = headlines.join(' ').toLowerCase();

  positiveWords.forEach(word => {
    const matches = (text.match(new RegExp(word, 'g')) || []).length;
    score += matches;
  });

  negativeWords.forEach(word => {
    const matches = (text.match(new RegExp(word, 'g')) || []).length;
    score -= matches;
  });

  // Normalize to -1 to 1 range
  return Math.max(-1, Math.min(1, score / Math.max(1, headlines.length)));
}

// Extract price data
const prices1h = candles1h.map(c => c[4]).filter(p => p > 0);
const prices4h = candles4h.map(c => c[4]).filter(p => p > 0);
const pricesDaily = candlesDaily.map(c => c[4]).filter(p => p > 0);

if (prices1h.length < 50 || prices4h.length < 25) {
  return {
    symbol: coinData.symbol,
    error: 'Insufficient data',
    data_points: { h1: prices1h.length, h4: prices4h.length }
  };
}

// Calculate indicators for different timeframes
const currentPrice = coinData.current_price;
const rsi1h = calculateRSI(prices1h);
const rsi4h = calculateRSI(prices4h);
const rsiDaily = calculateRSI(pricesDaily);

const macd1h = calculateMACD(prices1h);
const macd4h = calculateMACD(prices4h);

const ema20_1h = calculateEMA(prices1h, 20);
const ema50_1h = calculateEMA(prices1h, 50);
const ema20_4h = calculateEMA(prices4h, 20);
const ema50_4h = calculateEMA(prices4h, 50);
const ema200_daily = calculateEMA(pricesDaily, 50); // Use 50 for daily as we have limited data

// Parse news sentiment
const headlines = parseNewsForSentiment(newsHtml);
const sentimentScore = calculateSentimentScore(headlines, coinData);

// Enhanced scoring system (0-10 scale)
let longScore = 0;
let shortScore = 0;
let reasons = [];

// === LONG SIGNAL CRITERIA ===

// 1. RSI Oversold (Multi-timeframe confirmation)
if (rsi1h < 30 && rsi4h < 40) {
  longScore += 3;
  reasons.push('Multi-TF RSI oversold');
} else if (rsi1h < 35) {
  longScore += 1;
  reasons.push('1H RSI oversold');
}

// 2. MACD Bullish Cross
if (macd1h.macd > macd1h.signal && macd4h.macd > macd4h.signal) {
  longScore += 2;
  reasons.push('Multi-TF MACD bullish');
} else if (macd1h.macd > macd1h.signal) {
  longScore += 1;
  reasons.push('1H MACD cross up');
}

// 3. Price above key EMAs
if (currentPrice > ema20_1h && currentPrice > ema50_1h && currentPrice > ema200_daily) {
  longScore += 2;
  reasons.push('Above all EMAs');
} else if (currentPrice > ema20_1h) {
  longScore += 1;
  reasons.push('Above 20 EMA');
}

// 4. Positive sentiment
if (sentimentScore > 0.3) {
  longScore += 2;
  reasons.push('Strong positive news');
} else if (sentimentScore > 0) {
  longScore += 1;
  reasons.push('Positive sentiment');
}

// 5. Market structure support
if (coinData.marketContext.fear_greed > 40 && coinData.price_change_percentage_24h > -5) {
  longScore += 1;
  reasons.push('Market support');
}

// === SHORT SIGNAL CRITERIA ===

// 1. RSI Overbought
if (rsi1h > 80 && rsi4h > 70) {
  shortScore += 3;
  reasons.push('Multi-TF RSI overbought');
} else if (rsi1h > 75) {
  shortScore += 1;
  reasons.push('1H RSI overbought');
}

// 2. MACD Bearish Cross
if (macd1h.macd < macd1h.signal && macd4h.macd < macd4h.signal) {
  shortScore += 2;
  reasons.push('Multi-TF MACD bearish');
} else if (macd1h.macd < macd1h.signal) {
  shortScore += 1;
  reasons.push('1H MACD cross down');
}

// 3. Price below key EMAs
if (currentPrice < ema20_1h && currentPrice < ema50_1h && currentPrice < ema200_daily) {
  shortScore += 2;
  reasons.push('Below all EMAs');
} else if (currentPrice < ema20_1h) {
  shortScore += 1;
  reasons.push('Below 20 EMA');
}

// 4. Negative sentiment
if (sentimentScore < -0.3) {
  shortScore += 2;
  reasons.push('Strong negative news');
} else if (sentimentScore < 0) {
  shortScore += 1;
  reasons.push('Negative sentiment');
}

// 5. Market structure resistance
if (coinData.marketContext.fear_greed < 60 && coinData.price_change_percentage_24h < 5) {
  shortScore += 1;
  reasons.push('Market resistance');
}

// Determine signal quality
const minScore = 5; // Minimum score for signal
let signal = null;
let finalScore = 0;
let direction = null;

if (longScore >= minScore && longScore > shortScore) {
  signal = true;
  direction = 'LONG';
  finalScore = longScore;
} else if (shortScore >= minScore && shortScore > longScore) {
  signal = true;
  direction = 'SHORT';
  finalScore = shortScore;
}

// Calculate risk management levels
let entry, stopLoss, takeProfit1, takeProfit2;
if (signal) {
  const atr = Math.abs(prices1h[prices1h.length - 1] - prices1h[prices1h.length - 21]) / 20; // Simplified ATR
  const riskAmount = Math.max(atr, currentPrice * 0.02); // Min 2% risk

  entry = currentPrice;

  if (direction === 'LONG') {
    stopLoss = entry - (riskAmount * 2);
    takeProfit1 = entry + (riskAmount * 3);
    takeProfit2 = entry + (riskAmount * 5);
  } else {
    stopLoss = entry + (riskAmount * 2);
    takeProfit1 = entry - (riskAmount * 3);
    takeProfit2 = entry - (riskAmount * 5);
  }
}

return {
  ok: signal === true,
  symbol: coinData.symbol.toUpperCase(),
  name: coinData.name,
  direction: direction,
  entry: entry,
  stopLoss: stopLoss,
  takeProfit1: takeProfit1,
  takeProfit2: takeProfit2,
  confidence: signal ? Math.min(85, finalScore * 12 + 25) : 0,
  score: finalScore,
  reasons: reasons.slice(0, 4),
  technicals: {
    rsi_1h: rsi1h.toFixed(1),
    rsi_4h: rsi4h.toFixed(1),
    macd_1h: macd1h.histogram.toFixed(4),
    sentiment: sentimentScore.toFixed(2)
  },
  news_count: headlines.length,
  market_context: coinData.marketContext.fear_greed
};

// Compile final analysis and create trading message
const allAnalysis = $input.all();
const validSignals = allAnalysis.filter(item => item.json && item.json.ok === true);

function formatMessage() {
  if (validSignals.length > 0) {
    // High-conviction signals found
    let message = `üöÄ **HIGH-CONVICTION CRYPTO SETUPS**\n\n`;

    validSignals.forEach((signal, index) => {
      const data = signal.json;
      const riskReward = Math.abs((data.takeProfit1 - data.entry) / (data.entry - data.stopLoss)).toFixed(1);
      const stopRisk = Math.abs((data.entry - data.stopLoss) / data.entry * 100).toFixed(1);

      message += `**${index + 1}. ${data.symbol}** (${data.name}) ‚Äî **${data.direction}**\n`;
      message += `üí∞ Entry: $${data.entry.toFixed(data.entry < 1 ? 6 : 2)}\n`;
      message += `üõ°Ô∏è Stop: $${data.stopLoss.toFixed(data.stopLoss < 1 ? 6 : 2)} (${stopRisk}% risk)\n`;
      message += `üéØ TP1: $${data.takeProfit1.toFixed(data.takeProfit1 < 1 ? 6 : 2)}\n`;
      message += `üöÄ TP2: $${data.takeProfit2.toFixed(data.takeProfit2 < 1 ? 6 : 2)}\n`;
      message += `üìä Score: ${data.score}/10 | Confidence: ${data.confidence}%\n`;
      message += `üìà R:R = 1:${riskReward}\n`;
      message += `üí° **Key Factors:** ${data.reasons.join(', ')}\n`;
      message += `üìã RSI(1h/4h): ${data.technicals.rsi_1h}/${data.technicals.rsi_4h} | Sentiment: ${data.technicals.sentiment}\n\n`;
    });

    // Market context
    const marketFG = validSignals[0].json.market_context;
    message += `üìä **Market Context:** Fear & Greed ${marketFG}/100\n`;
    message += `‚è∞ _Analysis: ${new Date().toISOString().split('T')[0]} ${new Date().toTimeString().split(' ')[0].substring(0,5)} UTC_\n`;
    message += `‚ö†Ô∏è _Self-hosted analysis. Use proper risk management._`;

    return message;
  } else {
    // No signals - market overview
    const totalAnalyzed = allAnalysis.length;
    const avgScore = allAnalysis.reduce((sum, item) => {
      return sum + Math.max(item.json?.score || 0, 0);
    }, 0) / Math.max(1, totalAnalyzed);

    const topScorer = allAnalysis.reduce((best, current) => {
      const currentScore = Math.max(current.json?.score || 0, 0);
      const bestScore = Math.max(best?.json?.score || 0, 0);
      return currentScore > bestScore ? current : best;
    }, allAnalysis[0]);

    let message = `üìä **CRYPTO MARKET SCAN** (Self-Hosted)\n\n`;
    message += `‚ùå **No high-conviction signals** (min 5/10 score required)\n\n`;
    message += `üîç **Scan Results:**\n`;
    message += `‚Ä¢ Coins Analyzed: ${totalAnalyzed}\n`;
    message += `‚Ä¢ Avg Score: ${avgScore.toFixed(1)}/10\n`;

    if (topScorer?.json) {
      message += `‚Ä¢ Top Scorer: ${topScorer.json.symbol} (${topScorer.json.score || 0}/10)\n`;
    }

    const marketFG = topScorer?.json?.market_context || 50;
    message += `‚Ä¢ Fear & Greed: ${marketFG}/100\n\n`;

    // Recommendation based on market conditions
    if (marketFG < 25) {
      message += `üí° **Extreme Fear** - Potential buying opportunities developing`;
    } else if (marketFG > 75) {
      message += `‚ö†Ô∏è **Extreme Greed** - Exercise caution, consider profit-taking`;
    } else {
      message += `üòê **Neutral Market** - Wait for clearer directional signals`;
    }

    message += `\n\n‚è∞ _${new Date().toISOString().split('T')[0]} ${new Date().toTimeString().split(' ')[0].substring(0,5)} UTC_`;

    return message;
  }
}

return [{
  message: formatMessage(),
  signalCount: validSignals.length,
  timestamp: new Date().toISOString()
}];
